import React, { useState, useEffect } from 'react';
// import SearchBar from './SearchBar';
import Dashbotmain from './Dashbotmain';
import FilterOptions from './FilterOptions';

const Dashfilter = () => {

  const [events, setEvents] = useState([]);
  // const [searchTerm, setSearchTerm] = useState('');
  const [filterCategory, setFilterCategory] = useState('');

  // useEffect(() => {
    
  //   fetch('https://example.com/api/events')
  //     .then(response => response.json())
  //     .then(data => setEvents(data))
  //     .catch(error => console.error('Error fetching events:', error));
  // }, []);

  const customEvent = {
    id: 1,
    name: 'Custom Event',
    category: 'Conference',
    location: 'New York City',
    date: '2024-03-21',
    startTime: '09:00',
    endTime: '17:00',
    description: 'A conference on custom events',
    attendees: ['John Doe', 'Jane Smith'],
    organizer: 'Example Organization',
    imageUrl: 'https://example.com/event.jpg',
    website: 'https://example.com/event',
    tags: ['conference', 'tech', 'networking']
  };

  useEffect(()=>{
   setEvents(customEvent);
  },[])
  

  // const handleSearch = (term) => {
  //   setSearchTerm(term);
  // };

  const handleFilter = (category) => {
    setFilterCategory(category);
  };

  const filteredEvents = events.filter(event => {
    return (
      event.name.toLowerCase().includes(
      filterCategory === '' || event.category === filterCategory)
    );
  });

  return <>
    <div className='dashfil'>
      <FilterOptions onFilter={handleFilter} />
   </div>
   <div className="dashbotmain">
      <Dashbotmain  filteredEvents={filteredEvents}/>
    </div>
  </>
}

export default Dashfilter
