import React,{ useState, useEffect } from 'react';
import axios from 'axios';
import { useRefresh } from '../context/RefreshContext';
import { useUserContext } from '../context/UserContext';
import { useEventManagement } from '../context/EventMangementContext';

const MyEvents = () => {

  const [events, setEventList] = useState([]);
  const { UserId } = useUserContext();
  const [message, setMessage] = useState('Events Organised');
  const { refreshEventList,setRefreshEventList  } = useRefresh();
  const { setOrganizedEvents,setJoinedEvents } = useEventManagement();

  console.log("User in MyEvents-->",UserId);



  useEffect(() => {
        axios.get(`/api/getOrganisedEvents/${UserId}`)
            .then(response => {
              if (response.status === 200) {
                if (response.data.message === 'No Events Organised') {
                  setMessage(response.data.message);
              } else {
                  console.log("Response Data-->", response.data);
                  setEventList(response.data);
                  setMessage("Events Organised");
                  console.log("Eventlist", events);
              }
            }
            })
            .catch(error => {
                console.error('Error fetching event list:', error);
            })
  
}, [  refreshEventList,setRefreshEventList ]);

//handle delete function
const handleDelete = (deleteid) =>{
  axios.post(`/api/deleteMyEvent/${deleteid}`)
     .then(response =>{
      if(response.status === 200){
       setEventList(events.filter((item)=> item._id !== deleteid));
      if(events.length===0){
        setMessage("No Events Organised");
        setRefreshEventList(true);
      }
       alert("Deleted Successfully!!");
      }else{
        alert("Failed to Delete")
      }
    })
    .catch(error =>{
      console.log('Error Deleting event-->',error);
    })
}


//handle Event Count Upon Delete
useEffect(() => {
  handleEventCountUpdate();
}, [events, UserId]);

const handleEventCountUpdate = () => {
  let eventOrganizedCount = 0;
  let eventJoinedCount = 0;

  events.forEach(event => {
    if (event.organizer === UserId) {
      eventOrganizedCount++;
    }
    event.joined.forEach(user => {
      if (user === UserId) {
        eventJoinedCount++;
      }
    });
  });

  setOrganizedEvents(eventOrganizedCount);
  setJoinedEvents(eventJoinedCount);
};



  return (
    <div className='eventlistmain'>
    <h3 className='explore'>{message}</h3>
    <ul className='eventlist'>  
              {events.map(event => (
                  <li className='eventitem card' key={event._id}>
                       <div className="eventhead">
                       <div className="eventtitle">
                        <h2>{event.name}</h2>
                      </div>
                      <div className='joined'>
                         <img alt='' src="data:image/png;base64," />
                       <span>{event.joined > 0 ? event.joined : 0}</span>
                       </div>
                      </div>
                      <div className="eventmid">
                      <p>Category: {event.category}</p>
                      <p>Location: {event.location}</p>
                      <p>Date: {event.date}</p>
                      <p>Time: {event.startTime} - {event.endTime}</p>
                      <p className='desc'>Description: {event.description}</p>
                      <p>Attendees: {event.attendees}</p>
                      <p>Organizer: {event.organizerName}</p>
                      </div>
                      <div className="btn">
                        <button>Organized</button>
                        <button className='moreinfobtn'>More Info</button>
                        <button className='deletebtn moreinfobtn' onClick={()=>handleDelete(event._id)}></button>
                      </div>
                  </li>
              ))}
          </ul>
  </div>
  )
}

export default MyEvents
