import React,{useState, useEffect} from 'react'
import axios from 'axios';
import { useRefresh } from '../../context/RefreshContext';
import { useUserContext } from '../../context/UserContext';
import { useEventManagement } from '../../context/EventMangementContext';
const EventsJoined = () => {

    const [events, setEventList] = useState([]);
    const [message, setMessage] = useState('My Events');
    const { UserId, setMoreInfoId,  setMoreInfo  } = useUserContext();
    const { refreshEventList,setRefreshEventList } = useRefresh();
    const { setJoinedEvents } = useEventManagement();
  
    console.log("User in EventsJoined-->",UserId);
    
    // Fetching the events joined
    useEffect(() => {
      axios.get(`/api/getJoinedEvents/${UserId}`)
          .then(response => {
              if (response.status === 200) {
                  if (response.data.message === 'No Events Joined') {
                      setMessage(response.data.message);
                  } else {
                      console.log("Response Data-->", response.data);
                      setEventList(response.data);
                      setMessage("Events Joined");
                      console.log("Eventlist", events);
                  }
              }
          })
          .catch(error => {
              console.error('Error fetching event list:', error);
          });
  }, [refreshEventList,setRefreshEventList]);
  
  //leave event
 const leaveEventHandler = (eventid) => {
    let confirmLeaving = window.confirm('Are you sure to leave this Event?');
    if (confirmLeaving){
        axios.delete('/api/leaveEvent/'+ eventid+'/'+UserId)
         .then(response=>{
          if(response.status === 200){
            setEventList(events.filter((item)=> item._id !== eventid));
            console.log(response.data.message);
            alert(response.data.message);
            setRefreshEventList(false);
          }
         })
         .catch(error=>{
           console.log({error:"Error while leaving the event"});
         })
      }        
    }

    useEffect(() => {
      handleEventCountUpdate();
    }, [events, UserId]);

     
    const handleEventCountUpdate = () => {
      let eventJoinedCount = 0;
    
      events.forEach(event => {
        event.joined.forEach(user => {
          if (user === UserId) {
            eventJoinedCount++;
          }
        });
      });
    
      setJoinedEvents(eventJoinedCount);
    };

    const handleMoreInfo = (bool,id) =>{
      console.log("Bool->",bool,"ID->",id);
      setMoreInfo(bool);
      setMoreInfoId(id); 
     
    }
   
    
    
  return (
    <div className='eventlistmain'>
    <h3 className='explore'>{message}</h3>
    <ul className='eventlist'>
   
              {events.map(event => (
                  <li className='eventitem card' key={event._id}>
                       <div className="eventhead">
                       <div className="eventtitle">
                        <h2>{event.name}</h2>
                      </div>
                      <div className='joined'>
                         <img alt='' src="data:image/png;base64," />
                       <span><span>{event.joined.length}</span></span>
                       </div>
                      </div>
                      <div className="eventmid">
                      <p>Category: {event.category}</p>
                      <p>Location: {event.location}</p>
                      <p>Date: {event.date}</p>
                      <p>Time: {event.startTime} - {event.endTime}</p>
                      <p className='desc'>Description: {event.description}</p>
                      <p>Attendees: {event.attendees}</p>
                      <p>Organizer: {event.organizerName}</p>
                      </div>
                      <div className="btn">
                        <button onClick={()=>leaveEventHandler(event._id)}>Leave Event</button>
                        <button className='moreinfobtn' onClick={()=>handleMoreInfo(true,event.id)}>More Info</button>
                        {/* <button className='deletebtn moreinfobtn' onClick={()=>handleDelete(event._id)}></button> */}
                      </div>
                  </li>
              ))}
          </ul>
    </div>
  )
}

export default EventsJoined
